float erfinv(float x) {
	float tt1, tt2, lnx, sgn;
	sgn = (x < 0) ? -1.0f : 1.0f;

	x = (1 - x) * (1 + x);
	lnx = logf(x);

	tt1 = 2 / (3.14 * 0.147) + 0.5f * lnx;
	tt2 = 1 / (0.147) * lnx;

	return(sgn * sqrtf(-tt1 + sqrtf(tt1 * tt1 - tt2)));
};
float quantile(float ver)
{
	return sqrt(2) * erfinv(2 * ver - 1);
};

int N( float  p1 = 0.75f, float p2 = 0.5f)
{	
	
	long double res = pow((6.009353565530745 * sqrt(p2 * (1 - p2)) + quantile(0.99) * sqrt(p1 * (1 - p1))) / (p1 - p2), 2);
	return ceil(res);

}
