void ShiftAndEl30(array<int, 30> &l, int el)
{
	for (int i = 0; i < l.size()-1; i++)
	{
		l[i] = l[i + 1];
	}
	l[l.size() - 1] = el;
}

void ShiftAndEl31(array<int, 31>& l, int el)
{
	for (int i = 0; i < l.size() - 1; i++)
	{
		l[i] = l[i + 1];
	}
	l[l.size() - 1] = el;
}

void ShiftAndEl32(array<int, 32>& l, int el)
{
	for (int i = 0; i < l.size() - 1; i++)
	{
		l[i] = l[i + 1];
	}
	l[l.size() - 1] = el;
}
void ShiftAndElVec(int el, vector<int>& v)
{
	for (int i = 0; i < v.size() - 1; i++)
	{
		v[i] = v[i + 1];
	}
	v[v.size() - 1] = el;
}
