def count_mask(n):
    res = 0
    for i in range(n):
        res += 2**i
    return res
def quantile(ver): 
    return math.sqrt(2)*special.erfinv(2*ver-1)
  
def N(n, p1 = 1/4, p2 = 1/2):
    return int(round(math.pow((quantile(1-(1/(2**n)))*math.sqrt(p2*(1-p2))+quantile(0.99)*math.sqrt(p1*(1-p1)))/(p1-p2) , 2), 0))


def C(N, p1 = 1/4):
    return int(round(N*p1+quantile(0.99)*math.sqrt(N*p1*(1-p1))))
       
